        async function streamResponse() {  
            const response \= await puter.ai.chat(  
                "Explain the theory of relativity in detail",   
                {stream: true}  
            );  

            for await (const part of response) {  
                puter.print(part?.text);  
            }  
        }

        streamResponse();  
